// Author: Ahmed Moataz
function increaseFont(){
	var new_font = editor.getFontSize();
	if (new_font < 24) {
		new_font += 2;
		editor.setFontSize(parseInt(new_font));
	};	
}

// [Solve A Problem - Story 3.1]
// Decrease the font-size of the editor.
// Parameters: none
// Returns: none
// Author: Ahmed Moataz
