(function() {
  var activate, digit, min, time, timer, timer_is_on;

  digit = 0;

  min = 0;

  time = 0;

  timer_is_on = 0;

  timer = function() {
    var i, log, the_tip, tip, _results;
    digit++;
    if (digit > 59) {
      min++;
      document.getElementById("mins").innerHTML = min;
      digit = 0;
    }
    if (digit <= 9) {
      digit = "0" + digit;
    }
    document.getElementById("secs").innerHTML = digit;
    i = 0;
    _results = [];
    while (true) {
      tip = $('#tip' + i);
      if (typeof (tip.attr('time')) === 'undefined') {
        break;
      }
      if (typeof (tip.attr('shown')) === 'undefined') {
        time = tip.attr('time');
        if (time <= min * 60 + parseInt(digit, 10)) {
          tip.attr('class', '');
          the_tip = 'Tip: ' + tip.html();
          tip.attr('shown', '');
          log = alert(the_tip, 'log', 0);
        }
      }
      _results.push(i++);
    }
    return _results;
  };

  activate = function() {
    if (!timer_is_on) {
      timer_is_on = 1;
      time = setInterval(timer, 1000);
      return timer;
    }
  };

  jQuery(function() {
    if (element_exists("mins") && element_exists("secs")) {
      activate();
    }
  });

}).call(this);
